import React,{useState,useEffect} from "react";
import { Link } from "react-router-dom";
import "../stylling/blogs.css";
import axios from "axios";

const Blogs = () => {
  useEffect(() => {
    viewPost();
  }, []);

  const [ispost, setpost] = useState([]);
  const viewPost = async() =>{
    try {
      await axios.get(`http://localhost:8080/allPost`,)
      .then(res => { 
        if(res.data.success === true){
          setpost(res.data.listall);
        }
      })
    } catch (error) { throw error;}
  }

  return (
    <div className="blog__page">
      <h1><Link to="/addblogs">Add blogs</Link></h1>

      <div className="blogs">
      {ispost.map((item,index) => ( 
        <div className="post__list" key={index}>
        <h2>{item.title}</h2>

          <div className="post__description" dangerouslySetInnerHTML={{ __html: item.description}}  />
           <Link to={`/Edit/${item.id}`} className="btn btn__theme"> Edit </Link>

          </div>
       ))}

          <div className="blog">
            <img src="" />
            <div>
              <h3 className="sourceName">
                <span>title</span>
                <p>kdgfdkljgkldf</p>
              </h3>
              <h1>gkjdfgkjdfklg</h1>
              <p></p>
            </div>
          </div>




      </div>
    </div>
  );
};

export default Blogs;



