import React,{useState,useEffect} from "react";
import { Link } from "react-router-dom";
import "../stylling/blogs.css";
import axios from "axios";
import { EditorState } from "draft-js";
import { convertFromHTML } from "draft-convert";


const Blogs = () => {
  useEffect(() => {
    viewPost();
  }, []);

  const [ispost, setpost] = useState([]);
  const viewPost = async() =>{
    try {
      await axios.get("https://old-squid-82.loca.lt/api/postblog/getblog")
      .then(res => { 
        if(res.status === 200){
          console.log(first)
          const html =res.data.blog;

      const blocksFromHTML = convertFromHTML(html);

          setpost(EditorState.createWithContent(blocksFromHTML));
        }
      })
    } catch (error) { throw error;}
  }



  const onDelete = async (id) => {
    try {
      const url = "https://old-squid-82.loca.lt/api/postblog/getblog"+ id;
      const res = await axios.delete(url);
      alert("delete succefully !")
      viewPost();
    } catch {
      console.warn("error")
    }

  }


  return (
    <div className="blog__page">
      <h1><Link to="/addblogs">Add blogs</Link></h1>

      <div className="blogs" >
      {ispost.map((item,index)=>(
 <div className="blog">

            <img src={item.blogImage} />
            <div>
              <h3 className="sourceName">

                <p>{item.blogTitle}</p>
              </h3>

              <p>{item.Description}</p>
            </div>
            <button ><Link to={`/edit/${item._id}`}>Edit</Link></button> <button onClick={() => onDelete(item.id)}>Delete</button>
          </div>

     ))}

      </div>
    </div>
  );
};

export default Blogs;
