import React,{useState,useEffect} from "react";
import { Link } from "react-router-dom";
import "../stylling/blogs.css";
import axios from "axios";


const Blogs = () => {
  useEffect(() => {
    viewPost();
  }, []);

  const [ispost, setpost] = useState([]);
  const viewPost = async() =>{
    try {
      await axios.get("https://tidy-cow-99.loca.lt/api/postblog/getblog")
      .then(res => { 
        // console.log("^^^^^^^^^^^^",res)
        if(res.status === 200){
          setpost(res.data.blog);
        }

        // console.log("<<<<<<<<<<<<<<<<<<<<",ispost)
      })
    } catch (error) { throw error;}
  }
console.log(ispost)
  return (
    <div className="blog__page">
      <h1><Link to="/addblogs">Add blogs</Link></h1>

      <div className="blogs" >
      {ispost.map((item,index)=>(
 <div className="blog">
            <img src={item.blogImage} />
            <div>
              <h3 className="sourceName">

                <p>{item.blogTitle}</p>
              </h3>

              <p>{item.Description}</p>
            </div>
            <button ><Link to={`/edit/${item.id}`}>Edit</Link></button>
          </div>

     ))}

      </div>
    </div>
  );
};

export default Blogs;



